import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { 
  insertShapeSchema, 
  insertCanvasStateSchema, 
  insertChatMessageSchema,
  insertTaskSchema,
  insertTaskListSchema,
  insertLayoutSchema,
  insertBlockSchema,
  insertAppStateSchema
} from "@shared/schema";
import { groqService } from "./services/groq";
import multer from "multer";

const upload = multer({ storage: multer.memoryStorage() });

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket server for real-time communication
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws: WebSocket) => {
    console.log('Client connected to WebSocket');

    ws.on('message', async (data: Buffer) => {
      try {
        const message = JSON.parse(data.toString());

        switch (message.type) {
          case 'chat_message':
            await handleChatMessage(ws, message.content, message.appType || 'canvas');
            break;
          case 'voice_data':
            await handleVoiceData(ws, message.data, message.appType || 'canvas');
            break;
          case 'get_state':
            await sendCanvasState(ws);
            break;
          default:
            ws.send(JSON.stringify({ type: 'error', message: 'Unknown message type' }));
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
        ws.send(JSON.stringify({ type: 'error', message: 'Failed to process message' }));
      }
    });

    ws.on('close', () => {
      console.log('Client disconnected from WebSocket');
    });

    // Send initial state
    sendCanvasState(ws);
  });

  async function handleChatMessage(ws: WebSocket, content: string, appType: 'canvas' | 'tasks' | 'layout' = 'canvas') {
    // Save user message
    const userMessage = await storage.createChatMessage({
      content,
      sender: 'user',
      appType,
    });

    // Broadcast user message to all clients
    broadcast({ type: 'chat_message', message: userMessage });

    // Get current context based on app type
    let context = {};
    if (appType === 'canvas') {
      context = { shapes: await storage.getShapes() };
    } else if (appType === 'tasks') {
      context = { tasks: await storage.getTasks() };
    } else if (appType === 'layout') {
      context = { layouts: await storage.getLayouts() };
    }

    // Interpret command with AI
    const interpretation = await groqService.interpretCommand(content, context, appType);

    // Execute the interpreted command
    await executeCommand(interpretation, appType);

    // Save AI response
    const aiMessage = await storage.createChatMessage({
      content: interpretation.message,
      sender: 'ai',
      appType,
    });

    // Broadcast AI message and updated state
    broadcast({ type: 'chat_message', message: aiMessage });
    broadcast({ type: 'command_executed', interpretation });

    // Send updated state based on app type
    if (appType === 'canvas') {
      const updatedShapes = await storage.getShapes();
      broadcast({ type: 'shapes_updated', shapes: updatedShapes });
    } else if (appType === 'tasks') {
      const updatedTasks = await storage.getTasks();
      broadcast({ type: 'tasks_updated', tasks: updatedTasks });
    } else if (appType === 'layout') {
      const updatedLayouts = await storage.getLayouts();
      broadcast({ type: 'layouts_updated', layouts: updatedLayouts });
    }
  }

  async function handleVoiceData(ws: WebSocket, audioData: string, appType: 'canvas' | 'tasks' | 'layout' = 'canvas') {
    try {
      // Convert base64 to buffer
      const audioBuffer = Buffer.from(audioData, 'base64');

      // Transcribe with Whisper
      const transcription = await groqService.transcribeAudio(audioBuffer);

      if (transcription) {
        // Process as chat message
        await handleChatMessage(ws, transcription, appType);
        ws.send(JSON.stringify({ type: 'voice_transcribed', text: transcription }));
      }
    } catch (error) {
      console.error('Voice processing error:', error);
      ws.send(JSON.stringify({ type: 'error', message: 'Failed to process voice input' }));
    }
  }

  async function executeCommand(interpretation: any, appType: 'canvas' | 'tasks' | 'layout' = 'canvas') {
    try {
      if (appType === 'canvas') {
        switch (interpretation.action) {
          case 'draw':
            if (interpretation.shapes) {
              for (const shapeData of interpretation.shapes) {
                await storage.createShape(shapeData);
              }
            }
            break;
          case 'clear':
            await storage.clearShapes();
            break;
          case 'delete':
            await storage.clearShapes();
            break;
        }

        // Update canvas state
        const shapes = await storage.getShapes();
        await storage.updateCanvasState({
          shapes: shapes as any,
          mode: 'ai',
          lastCommand: interpretation.message,
        });

      } else if (appType === 'tasks') {
        switch (interpretation.action) {
          case 'create_task':
            if (interpretation.tasks) {
              for (const taskData of interpretation.tasks) {
                await storage.createTask({
                  title: taskData.title,
                  description: taskData.description,
                  priority: taskData.priority || 'medium',
                  category: taskData.category,
                  dueDate: taskData.dueDate ? new Date(taskData.dueDate) : null,
                  completed: taskData.completed || false,
                });
              }
            }
            break;
          case 'complete_task':
            if (interpretation.tasks) {
              for (const taskData of interpretation.tasks) {
                if (taskData.id) {
                  await storage.markTaskComplete(taskData.id, taskData.completed || true);
                }
              }
            }
            break;
          case 'update_task':
            if (interpretation.tasks) {
              for (const taskData of interpretation.tasks) {
                if (taskData.id) {
                  await storage.updateTask(taskData.id, taskData);
                }
              }
            }
            break;
        }

        // Update app state for tasks
        const tasks = await storage.getTasks();
        await storage.updateAppState('tasks', {
          appType: 'tasks',
          state: { tasks },
          mode: 'ai',
          lastCommand: interpretation.message,
        });

      } else if (appType === 'layout') {
        switch (interpretation.action) {
          case 'create_layout':
            if (interpretation.layout) {
              const newLayout = await storage.createLayout({
                name: interpretation.layout.name || 'AI Generated Layout',
                gridConfig: interpretation.layout.gridConfig,
                blocks: interpretation.layout.blocks || [],
              });
              // Broadcast layout creation
              broadcast({ type: 'layout_created', layout: newLayout });
            }
            break;
          case 'add_block':
            if (interpretation.layout && interpretation.layout.blocks) {
              for (const blockData of interpretation.layout.blocks) {
                // Find a layout to add blocks to (use first available or create one)
                const layouts = await storage.getLayouts();
                const targetLayout = layouts[0];

                if (targetLayout) {
                  const newBlock = await storage.createBlock({
                    type: blockData.type,
                    content: blockData.content,
                    position: blockData.position,
                    style: blockData.style || {},
                    layoutId: targetLayout.id,
                  });
                  // Broadcast block creation
                  broadcast({ type: 'block_created', block: newBlock });
                }
              }
            }
            break;
        }

        // Update app state for layouts
        const layouts = await storage.getLayouts();
        await storage.updateAppState('layout', {
          appType: 'layout',
          state: { layouts },
          mode: 'ai',
          lastCommand: interpretation.message,
        });
      }
    } catch (error) {
      console.error(`Failed to execute ${appType} command:`, error);
    }
  }

  async function sendCanvasState(ws: WebSocket) {
    const shapes = await storage.getShapes();
    const canvasState = await storage.getCanvasState();
    const messages = await storage.getChatMessages(20);

    ws.send(JSON.stringify({
      type: 'initial_state',
      shapes,
      canvasState,
      messages,
    }));
  }

  function broadcast(data: any) {
    wss.clients.forEach((client) => {
      if (client.readyState === WebSocket.OPEN) {
        client.send(JSON.stringify(data));
      }
    });
  }

  // REST API routes
  app.get('/api/shapes', async (req, res) => {
    try {
      const shapes = await storage.getShapes();
      res.json(shapes);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch shapes' });
    }
  });

  app.post('/api/shapes', async (req, res) => {
    try {
      const shapeData = insertShapeSchema.parse(req.body);
      const shape = await storage.createShape(shapeData);

      // Broadcast to WebSocket clients
      broadcast({ type: 'shape_created', shape });

      res.json(shape);
    } catch (error) {
      res.status(400).json({ error: 'Invalid shape data' });
    }
  });

  app.delete('/api/shapes/:id', async (req, res) => {
    try {
      const deleted = await storage.deleteShape(req.params.id);
      if (deleted) {
        broadcast({ type: 'shape_deleted', id: req.params.id });
        res.json({ success: true });
      } else {
        res.status(404).json({ error: 'Shape not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to delete shape' });
    }
  });

  app.delete('/api/shapes', async (req, res) => {
    try {
      await storage.clearShapes();
      broadcast({ type: 'shapes_cleared' });
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to clear shapes' });
    }
  });

  app.get('/api/canvas-state', async (req, res) => {
    try {
      const state = await storage.getCanvasState();
      res.json(state);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch canvas state' });
    }
  });

  app.get('/api/chat-messages', async (req, res) => {
    try {
      const messages = await storage.getChatMessages();
      res.json(messages);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch chat messages' });
    }
  });

  app.post('/api/chat-messages', async (req, res) => {
    try {
      const messageData = insertChatMessageSchema.parse(req.body);
      const message = await storage.createChatMessage(messageData);
      res.json(message);
    } catch (error) {
      res.status(400).json({ error: 'Invalid message data' });
    }
  });

  // Voice upload endpoint
  app.post('/api/voice-transcribe', upload.single('audio'), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ error: 'No audio file provided' });
      }

      const transcription = await groqService.transcribeAudio(req.file.buffer);
      res.json({ transcription });
    } catch (error) {
      res.status(500).json({ error: 'Failed to transcribe audio' });
    }
  });

  // Task management routes
  app.get('/api/tasks', async (req, res) => {
    try {
      const filters = {
        completed: req.query.completed ? req.query.completed === 'true' : undefined,
        priority: req.query.priority as string,
        category: req.query.category as string,
      };
      const tasks = await storage.getTasks(filters);
      res.json(tasks);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch tasks' });
    }
  });

  app.get('/api/tasks/:id', async (req, res) => {
    try {
      const task = await storage.getTask(req.params.id);
      if (task) {
        res.json(task);
      } else {
        res.status(404).json({ error: 'Task not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch task' });
    }
  });

  app.post('/api/tasks', async (req, res) => {
    try {
      const taskData = insertTaskSchema.parse(req.body);
      const task = await storage.createTask(taskData);
      broadcast({ type: 'task_created', task });
      res.json(task);
    } catch (error) {
      res.status(400).json({ error: 'Invalid task data' });
    }
  });

  app.patch('/api/tasks/:id', async (req, res) => {
    try {
      const task = await storage.updateTask(req.params.id, req.body);
      if (task) {
        broadcast({ type: 'task_updated', task });
        res.json(task);
      } else {
        res.status(404).json({ error: 'Task not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to update task' });
    }
  });

  app.delete('/api/tasks/:id', async (req, res) => {
    try {
      const deleted = await storage.deleteTask(req.params.id);
      if (deleted) {
        broadcast({ type: 'task_deleted', id: req.params.id });
        res.json({ success: true });
      } else {
        res.status(404).json({ error: 'Task not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to delete task' });
    }
  });

  // Task lists routes
  app.get('/api/task-lists', async (req, res) => {
    try {
      const taskLists = await storage.getTaskLists();
      res.json(taskLists);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch task lists' });
    }
  });

  app.post('/api/task-lists', async (req, res) => {
    try {
      const taskListData = insertTaskListSchema.parse(req.body);
      const taskList = await storage.createTaskList(taskListData);
      broadcast({ type: 'task_list_created', taskList });
      res.json(taskList);
    } catch (error) {
      res.status(400).json({ error: 'Invalid task list data' });
    }
  });

  // Layout management routes
  app.get('/api/layouts', async (req, res) => {
    try {
      const layouts = await storage.getLayouts();
      res.json(layouts);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch layouts' });
    }
  });

  app.get('/api/layouts/:id', async (req, res) => {
    try {
      const layout = await storage.getLayout(req.params.id);
      if (layout) {
        res.json(layout);
      } else {
        res.status(404).json({ error: 'Layout not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch layout' });
    }
  });

  app.post('/api/layouts', async (req, res) => {
    try {
      const layoutData = insertLayoutSchema.parse(req.body);
      const layout = await storage.createLayout(layoutData);
      broadcast({ type: 'layout_created', layout });
      res.json(layout);
    } catch (error) {
      res.status(400).json({ error: 'Invalid layout data' });
    }
  });

  app.patch('/api/layouts/:id', async (req, res) => {
    try {
      const layout = await storage.updateLayout(req.params.id, req.body);
      if (layout) {
        broadcast({ type: 'layout_updated', layout });
        res.json(layout);
      } else {
        res.status(404).json({ error: 'Layout not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to update layout' });
    }
  });

  app.delete('/api/layouts/:id', async (req, res) => {
    try {
      const deleted = await storage.deleteLayout(req.params.id);
      if (deleted) {
        broadcast({ type: 'layout_deleted', id: req.params.id });
        res.json({ success: true });
      } else {
        res.status(404).json({ error: 'Layout not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to delete layout' });
    }
  });

  // Block management routes
  app.get('/api/blocks', async (req, res) => {
    try {
      const layoutId = req.query.layoutId as string;
      const blocks = layoutId ? await storage.getBlocks(layoutId) : [];
      res.json(blocks);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch blocks' });
    }
  });

  app.get('/api/blocks/:id', async (req, res) => {
    try {
      const block = await storage.getBlock(req.params.id);
      if (block) {
        res.json(block);
      } else {
        res.status(404).json({ error: 'Block not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch block' });
    }
  });

  app.post('/api/blocks', async (req, res) => {
    try {
      const blockData = insertBlockSchema.parse(req.body);
      const block = await storage.createBlock(blockData);
      broadcast({ type: 'block_created', block });
      res.json(block);
    } catch (error) {
      res.status(400).json({ error: 'Invalid block data' });
    }
  });

  app.patch('/api/blocks/:id', async (req, res) => {
    try {
      const block = await storage.updateBlock(req.params.id, req.body);
      if (block) {
        broadcast({ type: 'block_updated', block });
        res.json(block);
      } else {
        res.status(404).json({ error: 'Block not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to update block' });
    }
  });

  app.delete('/api/blocks/:id', async (req, res) => {
    try {
      const deleted = await storage.deleteBlock(req.params.id);
      if (deleted) {
        broadcast({ type: 'block_deleted', id: req.params.id });
        res.json({ success: true });
      } else {
        res.status(404).json({ error: 'Block not found' });
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to delete block' });
    }
  });

  // App state routes
  app.get('/api/app-state/:appType', async (req, res) => {
    try {
      const appState = await storage.getAppState(req.params.appType);
      res.json(appState);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch app state' });
    }
  });

  app.post('/api/app-state/:appType', async (req, res) => {
    try {
      const stateData = insertAppStateSchema.parse(req.body);
      const appState = await storage.updateAppState(req.params.appType, stateData);
      broadcast({ type: 'app_state_updated', appState });
      res.json(appState);
    } catch (error) {
      res.status(400).json({ error: 'Invalid app state data' });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { 
  type User, type InsertUser, 
  type Shape, type InsertShape, 
  type CanvasState, type InsertCanvasState, 
  type ChatMessage, type InsertChatMessage,
  type Task, type InsertTask,
  type TaskList, type InsertTaskList,
  type Layout, type InsertLayout,
  type Block, type InsertBlock,
  type AppState, type InsertAppState
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Shape operations
  getShapes(): Promise<Shape[]>;
  getShape(id: string): Promise<Shape | undefined>;
  createShape(shape: InsertShape): Promise<Shape>;
  updateShape(id: string, shape: Partial<InsertShape>): Promise<Shape | undefined>;
  deleteShape(id: string): Promise<boolean>;
  clearShapes(): Promise<boolean>;

  // Canvas state operations
  getCanvasState(): Promise<CanvasState | undefined>;
  updateCanvasState(state: InsertCanvasState): Promise<CanvasState>;

  // Chat message operations
  getChatMessages(limit?: number, appType?: string): Promise<ChatMessage[]>;
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
  clearChatMessages(appType?: string): Promise<boolean>;

  // Task operations
  getTasks(filters?: { completed?: boolean; priority?: string; category?: string }): Promise<Task[]>;
  getTask(id: string): Promise<Task | undefined>;
  createTask(task: InsertTask): Promise<Task>;
  updateTask(id: string, task: Partial<InsertTask>): Promise<Task | undefined>;
  deleteTask(id: string): Promise<boolean>;
  markTaskComplete(id: string, completed: boolean): Promise<Task | undefined>;

  // Task list operations
  getTaskLists(): Promise<TaskList[]>;
  getTaskList(id: string): Promise<TaskList | undefined>;
  createTaskList(taskList: InsertTaskList): Promise<TaskList>;
  updateTaskList(id: string, taskList: Partial<InsertTaskList>): Promise<TaskList | undefined>;
  deleteTaskList(id: string): Promise<boolean>;

  // Layout operations
  getLayouts(): Promise<Layout[]>;
  getLayout(id: string): Promise<Layout | undefined>;
  createLayout(layout: InsertLayout): Promise<Layout>;
  updateLayout(id: string, layout: Partial<InsertLayout>): Promise<Layout | undefined>;
  deleteLayout(id: string): Promise<boolean>;

  // Block operations
  getBlocks(layoutId: string): Promise<Block[]>;
  getBlock(id: string): Promise<Block | undefined>;
  createBlock(block: InsertBlock): Promise<Block>;
  updateBlock(id: string, block: Partial<InsertBlock>): Promise<Block | undefined>;
  deleteBlock(id: string): Promise<boolean>;

  // App state operations
  getAppState(appType: string): Promise<AppState | undefined>;
  updateAppState(appType: string, state: InsertAppState): Promise<AppState>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private shapes: Map<string, Shape>;
  private canvasState: CanvasState | undefined;
  private chatMessages: Map<string, ChatMessage>;
  private tasks: Map<string, Task>;
  private taskLists: Map<string, TaskList>;
  private layouts: Map<string, Layout>;
  private blocks: Map<string, Block>;
  private appStates: Map<string, AppState>;

  constructor() {
    this.users = new Map();
    this.shapes = new Map();
    this.chatMessages = new Map();
    this.tasks = new Map();
    this.taskLists = new Map();
    this.layouts = new Map();
    this.blocks = new Map();
    this.appStates = new Map();

    this.canvasState = {
      id: randomUUID(),
      shapes: [],
      mode: 'manual',
      lastCommand: null,
      updatedAt: new Date(),
    };

    // Initialize default app states
    this.appStates.set('tasks', {
      id: randomUUID(),
      appType: 'tasks',
      state: { tasks: [], taskLists: [] },
      mode: 'manual',
      lastCommand: null,
      updatedAt: new Date(),
      userId: null,
    });

    this.appStates.set('layout', {
      id: randomUUID(),
      appType: 'layout',
      state: { layouts: [], blocks: [] },
      mode: 'manual',
      lastCommand: null,
      updatedAt: new Date(),
      userId: null,
    });
  }

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  // Shape operations
  async getShapes(): Promise<Shape[]> {
    return Array.from(this.shapes.values());
  }

  async getShape(id: string): Promise<Shape | undefined> {
    return this.shapes.get(id);
  }

  async createShape(insertShape: InsertShape): Promise<Shape> {
    const id = randomUUID();
    const shape: Shape = {
      id,
      type: insertShape.type,
      x: insertShape.x,
      y: insertShape.y,
      width: insertShape.width || null,
      height: insertShape.height || null,
      radius: insertShape.radius || null,
      x2: insertShape.x2 || null,
      y2: insertShape.y2 || null,
      color: insertShape.color || '#000000',
      strokeWidth: insertShape.strokeWidth || 2,
      createdAt: new Date(),
      userId: insertShape.userId || null,
    };
    this.shapes.set(id, shape);
    return shape;
  }

  async updateShape(id: string, updateData: Partial<InsertShape>): Promise<Shape | undefined> {
    const existing = this.shapes.get(id);
    if (!existing) return undefined;

    const updated: Shape = { ...existing, ...updateData };
    this.shapes.set(id, updated);
    return updated;
  }

  async deleteShape(id: string): Promise<boolean> {
    return this.shapes.delete(id);
  }

  async clearShapes(): Promise<boolean> {
    this.shapes.clear();
    return true;
  }

  // Canvas state operations
  async getCanvasState(): Promise<CanvasState> {
    return this.canvasState!;
  }

  async updateCanvasState(state: InsertCanvasState): Promise<CanvasState> {
    this.canvasState = {
      id: this.canvasState?.id || randomUUID(),
      mode: state.mode || 'manual',
      shapes: state.shapes || [],
      lastCommand: state.lastCommand || null,
      updatedAt: new Date(),
    };
    return this.canvasState;
  }

  // Chat message operations
  async getChatMessages(limit: number = 50, appType?: string): Promise<ChatMessage[]> {
    const messages = Array.from(this.chatMessages.values())
      .filter(msg => !appType || msg.appType === appType)
      .sort((a, b) => a.timestamp!.getTime() - b.timestamp!.getTime());

    return limit ? messages.slice(-limit) : messages;
  }

  async createChatMessage(insertMessage: InsertChatMessage): Promise<ChatMessage> {
    const id = randomUUID();
    const message: ChatMessage = {
      ...insertMessage,
      id,
      timestamp: new Date(),
      processed: false,
    };
    this.chatMessages.set(id, message);
    return message;
  }

  async clearChatMessages(appType?: string): Promise<boolean> {
    if (appType) {
      const toDelete = Array.from(this.chatMessages.entries())
        .filter(([_, msg]) => msg.appType === appType)
        .map(([id, _]) => id);
      toDelete.forEach(id => this.chatMessages.delete(id));
    } else {
      this.chatMessages.clear();
    }
    return true;
  }

  // Task operations
  async getTasks(filters?: { completed?: boolean; priority?: string; category?: string }): Promise<Task[]> {
    let tasks = Array.from(this.tasks.values());

    if (filters) {
      if (filters.completed !== undefined) {
        tasks = tasks.filter(task => task.completed === filters.completed);
      }
      if (filters.priority) {
        tasks = tasks.filter(task => task.priority === filters.priority);
      }
      if (filters.category) {
        tasks = tasks.filter(task => task.category === filters.category);
      }
    }

    return tasks.sort((a, b) => a.createdAt!.getTime() - b.createdAt!.getTime());
  }

  async getTask(id: string): Promise<Task | undefined> {
    return this.tasks.get(id);
  }

  async createTask(insertTask: InsertTask): Promise<Task> {
    const id = randomUUID();
    const task: Task = {
      id,
      title: insertTask.title,
      description: insertTask.description || null,
      completed: insertTask.completed || false,
      priority: insertTask.priority || 'medium',
      dueDate: insertTask.dueDate || null,
      category: insertTask.category || null,
      tags: insertTask.tags || [],
      createdAt: new Date(),
      updatedAt: new Date(),
      userId: insertTask.userId || null,
    };
    this.tasks.set(id, task);
    return task;
  }

  async updateTask(id: string, updateData: Partial<InsertTask>): Promise<Task | undefined> {
    const existing = this.tasks.get(id);
    if (!existing) return undefined;

    const updated: Task = { 
      ...existing, 
      ...updateData,
      updatedAt: new Date(),
    };
    this.tasks.set(id, updated);
    return updated;
  }

  async deleteTask(id: string): Promise<boolean> {
    return this.tasks.delete(id);
  }

  async markTaskComplete(id: string, completed: boolean): Promise<Task | undefined> {
    return this.updateTask(id, { completed });
  }

  // Task list operations
  async getTaskLists(): Promise<TaskList[]> {
    return Array.from(this.taskLists.values());
  }

  async getTaskList(id: string): Promise<TaskList | undefined> {
    return this.taskLists.get(id);
  }

  async createTaskList(insertTaskList: InsertTaskList): Promise<TaskList> {
    const id = randomUUID();
    const taskList: TaskList = {
      id,
      name: insertTaskList.name,
      description: insertTaskList.description || null,
      color: insertTaskList.color || '#3B82F6',
      tasks: insertTaskList.tasks || [],
      createdAt: new Date(),
      userId: insertTaskList.userId || null,
    };
    this.taskLists.set(id, taskList);
    return taskList;
  }

  async updateTaskList(id: string, updateData: Partial<InsertTaskList>): Promise<TaskList | undefined> {
    const existing = this.taskLists.get(id);
    if (!existing) return undefined;

    const updated: TaskList = { ...existing, ...updateData };
    this.taskLists.set(id, updated);
    return updated;
  }

  async deleteTaskList(id: string): Promise<boolean> {
    return this.taskLists.delete(id);
  }

  // Layout operations
  async getLayouts(): Promise<Layout[]> {
    return Array.from(this.layouts.values());
  }

  async getLayout(id: string): Promise<Layout | undefined> {
    return this.layouts.get(id);
  }

  async createLayout(insertLayout: InsertLayout): Promise<Layout> {
    const id = randomUUID();
    const layout: Layout = {
      id,
      name: insertLayout.name,
      description: insertLayout.description || null,
      gridConfig: insertLayout.gridConfig,
      blocks: insertLayout.blocks || [],
      createdAt: new Date(),
      updatedAt: new Date(),
      userId: insertLayout.userId || null,
    };
    this.layouts.set(id, layout);
    return layout;
  }

  async updateLayout(id: string, updateData: Partial<InsertLayout>): Promise<Layout | undefined> {
    const existing = this.layouts.get(id);
    if (!existing) return undefined;

    const updated: Layout = { 
      ...existing, 
      ...updateData,
      updatedAt: new Date(),
    };
    this.layouts.set(id, updated);
    return updated;
  }

  async deleteLayout(id: string): Promise<boolean> {
    return this.layouts.delete(id);
  }

  // Block operations
  async getBlocks(layoutId: string): Promise<Block[]> {
    return Array.from(this.blocks.values()).filter(block => block.layoutId === layoutId);
  }

  async getBlock(id: string): Promise<Block | undefined> {
    return this.blocks.get(id);
  }

  async createBlock(insertBlock: InsertBlock): Promise<Block> {
    const id = randomUUID();
    const block: Block = {
      id,
      type: insertBlock.type,
      content: insertBlock.content,
      position: insertBlock.position,
      style: insertBlock.style || {},
      layoutId: insertBlock.layoutId,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.blocks.set(id, block);
    return block;
  }

  async updateBlock(id: string, updateData: Partial<InsertBlock>): Promise<Block | undefined> {
    const existing = this.blocks.get(id);
    if (!existing) return undefined;

    const updated: Block = { 
      ...existing, 
      ...updateData,
      updatedAt: new Date(),
    };
    this.blocks.set(id, updated);
    return updated;
  }

  async deleteBlock(id: string): Promise<boolean> {
    return this.blocks.delete(id);
  }

  // App state operations
  async getAppState(appType: string): Promise<AppState | undefined> {
    return this.appStates.get(appType);
  }

  async updateAppState(appType: string, stateData: InsertAppState): Promise<AppState> {
    const existing = this.appStates.get(appType);
    const appState: AppState = {
      id: existing?.id || randomUUID(),
      appType: stateData.appType || appType,
      state: stateData.state,
      mode: stateData.mode || 'manual',
      lastCommand: stateData.lastCommand || null,
      updatedAt: new Date(),
      userId: stateData.userId || null,
    };
    this.appStates.set(appType, appState);
    return appState;
  }
}

export const storage = new MemStorage();

