  selectedTool,
  selectedColor,
  onModeChange,
  onToolChange,
  onColorChange,
  onDeleteSelected,
  onClearCanvas,
}: ToolSidebarProps) {
  return (
    <div className="w-16 bg-white border-r border-gray-200 flex flex-col items-center py-4 space-y-3">
      {/* Mode Toggle */}
      <div className="mb-4">
        <div className="bg-gray-100 rounded-lg p-1 flex flex-col space-y-1">
          <Button
            size="sm"
